@bot.message_handler(commands=['start'])
def start_command(message):
    markup = types.InlineKeyboardMarkup(row_width=2)
    markup.add(*[types.InlineKeyboardButton(car.upper(), callback_data=f'car_{car}') for car in cars_dict.keys()])

    bot.send_message(message.chat.id, "Привет! Я твой помощник в выборе машин. Какую марку выбираешь?", reply_markup=markup)     

@bot.callback_query_handler(func=lambda call: call.data.startswith('car_'))
def car_handler(call):
    car_choice = call.data.split('_')[1]
    car_models_lst = cars_dict.get(car_choice).keys()

    markup = types.InlineKeyboardMarkup(row_width=2)
    markup.add(*[types.InlineKeyboardButton(model.upper(), callback_data=f'model_{car_choice}_{model}') for model in car_models_lst])
    bot.edit_message_text("Отличный выбор! О какой модели этой марки ты хочешь узнать?", 
                         call.message.chat.id, 
                         call.message.message_id, 
                         reply_markup=markup)

@bot.callback_query_handler(func=lambda call: call.data.startswith('model_'))
def model_handler(call):
    car_choice = call.data.split('_')[1]
    model_choice = call.data[call.data.find('_', call.data.find('_') + 1) + 1:]
    car_info = cars_dict.get(car_choice).get(model_choice)

    markup = types.InlineKeyboardMarkup()
    markup.add(types.InlineKeyboardButton("Выбрать другую машину", callback_data="new_choice"))
    

    bot.delete_message(call.message.chat.id, call.message.message_id)
    try:
        bot.send_photo(call.message.chat.id,
                    photo=car_info.get("photo"),
                    caption=car_info.get("info"),
                    reply_markup=markup)
    except Exception as err:
        bot.send_message(call.message.chat.id, 'К сожалению, что-то пошло не так. Попробуйте еще раз.', reply_markup=markup)
    
@bot.callback_query_handler(func=lambda call: call.data == "new_choice")
def new_choice_handler(call):
    bot.delete_message(call.message.chat.id, call.message.message_id)
    start_command(call.message)

bot.polling(none_stop=True, interval=0)
